section.script:
$( document ).ready(function() {
  var documentsAll = 0;
  var documentsFiltered = 0;
  $.ajax({
    type: "GET",
    url: "../api/v1/search?q=objectType:mods AND state:published AND NOT(mods.findable:true)&rows=0&wt=json&json.wrf=?",
    dataType: "jsonp",
    success: function (data) {
      documentsFiltered = $.number(data.response.numFound,0, ',', '.');
      if ( documentsFiltered ) {
        $('#leo-searchInput').attr('placeholder', 'Suche in ' + documentsFiltered + ' Dokumenten');
      }

    },
  });
  $.ajax({
    type: "GET",
    url: "../api/v1/search?q=objectType:mods AND state:published&rows=0&wt=json&json.wrf=?",
    dataType: "jsonp",
    success: function (data) {
      documentsAll = $.number(data.response.numFound,0, ',', '.');
    },
  });
  var findableFilter = '';
  $("#leo-searchMainPage").append( findableFilter );

  $("#switchFindable").change(function() {
    if ( $("#switchFindable").prop("checked") === true ) {
      $("#filterFindable").remove();
      if ( documentsAll ) {
        $('#leo-searchInput').attr('placeholder', 'Suche in ' + documentsAll + ' Dokumenten');
      }
    } else {
      $("#leo-searchMainPage").append( findableFilter );
      if ( documentsFiltered ) {
        $('#leo-searchInput').attr('placeholder', 'Suche in ' + documentsFiltered + ' Dokumenten');
      }
    }
  });
});

